import ee
import os
from pprint import pprint
ee.Authenticate()
ee.Initialize()

"""# **STEP 3**
## **3.0 Function** 
  ## **3.1 Set assets previously performed** // or Set your workspace directory
  ## **3.2 Aplly functions to sum in areas of transition annual** 
  ## **3.3 Set your drive Google Drive** 


"""

## 3.0 Function 
def start(years):

    print(years)

## 3.1 Set Assets 
    # Set your region of interest in raster, or all Biomes_BR (IBGE, 2020) 
    biomas = ee.Image(
        'projects/ee-seeg-brazil/assets/collection_9/v1/Biomes_BR_tif')

    # Set Municipalities_BR
    municipios = ee.Image(
        'projects/ee-seeg-brazil/assets/collection_9/v1/mun_BR')

    # Set your Asset ImagemCollection step_4.1 previously performed : eg. path 'projects/ee-seeg-brazil/assets/collection_9/v1/4_1_SEEG_Transitions_stacked'
    transitions = ee.Image(
        'projects/ee-seeg-brazil/assets/collection_9/v1/4_1_SEEG_Transitions_stacked')
    
    # Here is the multi-band raster of protected areas (PA), where each band is the cumulative of PA areas and units in each year
    apMask = ee.Image(
        'projects/mapbiomas-workspace/AUXILIAR/areas-protegidas-por-ano-2019/ap' + years[0]).unmask()

    biomasMunicip = biomas.multiply(10000000).add(municipios)

    geometry = biomas.geometry().bounds()
    # Create a bounding box BRAZIL 
    geometry = ee.Geometry.Polygon(
        [[[-74.34040691705002, 5.9630086351511690],
                [-74.34040691705002, -34.09134700746099],
                [-33.64704754205002, -34.09134700746099],
                [-33.64704754205002, 5.9630086351511690]]])

    pixelArea = ee.Image.pixelArea().divide(1000000)

## 3.2 Aplly functions to sum in areas of transition annual 

    # Region Calculation: This function sums the areas per region
    def getPropertiesAp0(item):

        item = ee.Dictionary(item)

        year = ee.Dictionary(ee.List(item.get('groups')).get(0)).get('ANO')

        feature = ee.Feature(None) \
            .set("featureid", ee.String(item.get('featureid'))) \
            .set("ANO", year) \
            .set("AP", 0) \
            .set("data", [])

        areasList = ee.List(ee.Dictionary(
            ee.List(item.get('groups')).get(0)).get('groups'))

        def temp(obj, feature):
            obj = ee.Dictionary(obj)

            classe = ee.String(ee.Number(obj.get('CLASSE')).toUint32())

            area = obj.get('sum')

            datalist = ee.List(ee.Feature(feature).get(
                'data')).add([classe, area])

            return ee.Feature(feature).set('data', datalist)

        feature = areasList.iterate(
            temp, feature
        )

        return feature

    def getPropertiesAp1(item):

        item = ee.Dictionary(item)

        year = ee.Dictionary(ee.List(item.get('groups')).get(0)).get('ANO')

        feature = ee.Feature(None) \
            .set("featureid", ee.String(item.get('featureid'))) \
            .set("ANO", year) \
            .set("AP", 1) \
            .set("data", [])

        areasList = ee.List(ee.Dictionary(
            ee.List(item.get('groups')).get(0)).get('groups'))

        def temp(obj, feature):
            obj = ee.Dictionary(obj)

            classe = ee.String(ee.Number(obj.get('CLASSE')).toUint32())

            area = obj.get('sum')

            datalist = ee.List(ee.Feature(feature).get(
                'data')).add([classe, area])

            return ee.Feature(feature).set('data', datalist)

        feature = areasList.iterate(
            temp, feature
        )

        return feature

    def calculateArea(image, regions, feature, ap, year1, year2, apClass):

        reducer = ee.Reducer.sum().group(1, 'CLASSE').group(1, 'ANO').group(1, 'featureid')

        areas = pixelArea.addBands(regions).addBands(ee.Image((year1 * 10000) + year2)).addBands(image) \
            .mask(ap.eq(apClass))\
            .reduceRegion(
                reducer=reducer,
                geometry=geometry,
                scale=30,
                maxPixels=1e12
        )

        if apClass:
            collection = ee.FeatureCollection(
                ee.List(areas.get('groups'))
                .map(getPropertiesAp1)
            )
        else:
            collection = ee.FeatureCollection(
                ee.List(areas.get('groups'))
                .map(getPropertiesAp0)
            )

        return collection

    areasAp0 = []
    areasAp1 = []

    image = transitions.select(['transicao_' + years[0] + '_' + years[1]])

    areasAp0.append(calculateArea(image, biomasMunicip, geometry,
                                  apMask, int(years[0]), int(years[1]), 0))
    areasAp1.append(calculateArea(image, biomasMunicip, geometry,
                                  apMask, int(years[0]), int(years[1]), 1))

    areasAp0 = ee.FeatureCollection(areasAp0).flatten()
    areasAp1 = ee.FeatureCollection(areasAp1).flatten()

    areas = areasAp0.merge(areasAp1)

## 3.3 Set your drive Google Drive  

    name = "SEEG_BR_c9_v1_"+ \
        years[0]+'-'+ years [1]

    task = ee.batch.Export.table.toDrive(
        collection=areas,
        description=name,
        folder='SEEG_c9_v1', #Export to your Google Drive or other's path 
        fileNamePrefix=name,
        fileFormat="GeoJSON")           

    task.start()
periods = [
    ["1989", "1990"],
    ["1990", "1991"],
    ["1991", "1992"],
    ["1992", "1993"],
    ["1993", "1994"],
    ["1994", "1995"],
    ["1995", "1996"],
    ["1996", "1997"],
    ["1997", "1998"],
    ["1998", "1999"],
    ["1999", "2000"],
    ["2000", "2001"],
    ["2001", "2002"],
    ["2002", "2003"],
    ["2003", "2004"],
    ["2004", "2005"],
    ["2005", "2006"],
    ["2006", "2007"],
    ["2007", "2008"],
    ["2008", "2009"],
    ["2009", "2010"],
    ["2010", "2011"],
    ["2011", "2012"],
    ["2012", "2013"],
    ["2013", "2014"],
    ["2014", "2015"],
    ["2015", "2016"],
    ["2016", "2017"],
    ["2017","2018"],
    ["2018","2019"],
    ["2019","2020"]
]

for period in periods:
    start(period)



